T = int(input())for t in range(1, T+1):    N, K = map(int, input().split())    # 4, 3    element = list(map(int, input().split()))   # [1, 2, 1, 2]    print(element)    cnt = 0    for i in range(1, 1<<N):    # i = 1, 2, 3, 4, 5,...,15        sum = 0        for j in range(N):  # j = 0,1,2,3            if i & (1<<j):                print(i, j, 1<<j)                print(element[j])                sum += element[j]        if sum == K:            cnt += 1    print(f'#{t}', cnt)